GtkWidget *
create_win_bg(void)
{
   GtkWidget *win_bg;
   GtkWidget *vbox13;
   GtkWidget *hbox15;
   GtkWidget *evas;
   GtkWidget *yscale;
   GtkWidget *xscale;
   GtkWidget *_ebony_statusbar;

   win_bg = gtk_window_new(GTK_WINDOW_TOPLEVEL);
   gtk_widget_set_name(win_bg, "win_bg");
   gtk_object_set_data(GTK_OBJECT(win_bg), "win_bg", win_bg);
   gtk_window_set_title(GTK_WINDOW(win_bg), _("Filename: "));
   gtk_window_set_policy(GTK_WINDOW(win_bg), TRUE, TRUE, FALSE);
   gtk_window_set_wmclass(GTK_WINDOW(win_bg), "Ebony", "Background");

   vbox13 = gtk_vbox_new(FALSE, 0);
   gtk_widget_set_name(vbox13, "vbox13");
   gtk_widget_ref(vbox13);
   gtk_object_set_data_full(GTK_OBJECT(win_bg), "vbox13", vbox13,
                            (GtkDestroyNotify) gtk_widget_unref);
   gtk_widget_show(vbox13);
   gtk_container_add(GTK_CONTAINER(win_bg), vbox13);
   gtk_container_set_border_width(GTK_CONTAINER(vbox13), 2);

   hbox15 = gtk_hbox_new(FALSE, 0);
   gtk_widget_set_name(hbox15, "hbox15");
   gtk_widget_ref(hbox15);
   gtk_object_set_data_full(GTK_OBJECT(win_bg), "hbox15", hbox15,
                            (GtkDestroyNotify) gtk_widget_unref);
   gtk_widget_show(hbox15);
   gtk_box_pack_start(GTK_BOX(vbox13), hbox15, TRUE, TRUE, 0);
   gtk_container_set_border_width(GTK_CONTAINER(hbox15), 1);

   evas = gtk_drawing_area_new();
   gtk_widget_set_name(evas, "evas");
   gtk_widget_ref(evas);
   gtk_object_set_data_full(GTK_OBJECT(win_bg), "evas", evas,
                            (GtkDestroyNotify) gtk_widget_unref);
   gtk_widget_show(evas);
   gtk_box_pack_start(GTK_BOX(hbox15), evas, TRUE, TRUE, 2);
   gtk_widget_set_usize(evas, 200, 200);
   gtk_widget_set_events(evas, GDK_EXPOSURE_MASK);

   yscale =
      gtk_vscale_new(GTK_ADJUSTMENT(gtk_adjustment_new(0, 0, 1024, 0, 0, 0)));
   gtk_widget_set_name(yscale, "yscale");
   gtk_widget_ref(yscale);
   gtk_object_set_data_full(GTK_OBJECT(win_bg), "yscale", yscale,
                            (GtkDestroyNotify) gtk_widget_unref);
   gtk_widget_show(yscale);
   gtk_box_pack_start(GTK_BOX(hbox15), yscale, FALSE, FALSE, 0);

   xscale =
      gtk_hscale_new(GTK_ADJUSTMENT(gtk_adjustment_new(0, 0, 1024, 0, 0, 0)));
   gtk_widget_set_name(xscale, "xscale");
   gtk_widget_ref(xscale);
   gtk_object_set_data_full(GTK_OBJECT(win_bg), "xscale", xscale,
                            (GtkDestroyNotify) gtk_widget_unref);
   gtk_widget_show(xscale);
   gtk_box_pack_start(GTK_BOX(vbox13), xscale, FALSE, FALSE, 0);

   _ebony_statusbar = gtk_statusbar_new();
   gtk_widget_set_name(_ebony_statusbar, "_ebony_statusbar");
   gtk_widget_ref(_ebony_statusbar);
   gtk_object_set_data_full(GTK_OBJECT(win_bg), "_ebony_statusbar",
                            _ebony_statusbar,
                            (GtkDestroyNotify) gtk_widget_unref);
   gtk_widget_show(_ebony_statusbar);
   gtk_box_pack_start(GTK_BOX(vbox13), _ebony_statusbar, FALSE, FALSE, 0);

   gtk_signal_connect(GTK_OBJECT(win_bg), "delete_event",
                      GTK_SIGNAL_FUNC(on_win_bg_delete_event), NULL);
   gtk_signal_connect(GTK_OBJECT(win_bg), "key_press_event",
                      GTK_SIGNAL_FUNC(on_win_bg_button_press_event), NULL);
   gtk_signal_connect(GTK_OBJECT(evas), "configure_event",
                      GTK_SIGNAL_FUNC(on_bg_evas_configure_event), NULL);
   gtk_signal_connect(GTK_OBJECT(evas), "drag_data_received",
                      GTK_SIGNAL_FUNC(on_bg_evas_drag_data_received), NULL);
   gtk_signal_connect(GTK_OBJECT(evas), "expose_event",
                      GTK_SIGNAL_FUNC(on_evas_expose_event), NULL);

   return win_bg;
}
